mapper.select:
SELECT ROWNUM AS RNUM, ZIP_ID, ZIP_TITLE, ZIP_CREATED_AT, USERS_ID
FROM ZIP_BOARD
WHERE USERS_ID = #{usersId}

SELECT ROWNUM AS RNUM, R.REVIEW_ID, R.REVIEW_TITLE, R.REVIEW_CREATED_AT, R.USERS_ID
FROM REVIEW R
WHERE R.USERS_ID = #{usersId}

SELECT ROWNUM AS RNUM, DN_ID,DN_PRDUCT_CATEGORY,DN_TITLE,USERS_ID
FROM DN_BOARD
WHERE USERS_ID=#{usersId}

SELECT USERS_ID, LOGIN_ID, PASSWORD, NAME, NICKNAME, EMAIL, PHONE_NUMBER, ADDRESS, ADDRESS_DETAIL
FROM USERS
WHERE USERS_ID=#{usersId}

mapper.update:
UPDATE USERS
SET PASSWORD=#{password}, NAME=#{name}, NICKNAME=#{nickname}, EMAIL=#{email}, PHONE_NUMBER=#{phoneNumber},
    ADDRESS=#{address}, ADDRESS_DETAIL=#{addressDetail},ZIP_CODE=#{zipCode}
WHERE USERS_ID=#{usersId}

mapper.select:
SELECT RNUM, QNA_ID, QNA_TITLE, QNA_CREATED_AT, QNA_ANSWER_COUNT, USERS_ID FROM( SELECT ROWNUM AS RNUM, QNA_ID, QNA_TITLE, QNA_CREATED_AT, QNA_ANSWER_COUNT, USERS_ID FROM( SELECT qb.QNA_ID, qb.QNA_TITLE, qb.QNA_CREATED_AT, (SELECT COUNT(*) FROM QNA_ANSWER qa WHERE qa.QNA_ID = qb.QNA_ID) AS QNA_ANSWER_COUNT, USERS_ID FROM QNA_BOARD qb WHERE USERS_ID = #{usersId} )
WHERE ROWNUM <= #{page.page} * #{page.size}
) WHERE RNUM > (#{page.page} - 1) * #{page.size}

SELECT COUNT(*)
FROM QNA_BOARD
WHERE USERS_ID=#{usersId}

    SELECT  RNUM,HELP_ID, USERS_ID, HELP_TITLE, HELP_CARE_TYPE, HELP_CARE_DATE, HELP_CREATED_AT
    FROM(
     SELECT ROWNUM AS RNUM,HELP_ID, USERS_ID, HELP_TITLE, HELP_CARE_TYPE, HELP_CARE_DATE, HELP_CREATED_AT
         FROM(
SELECT HELP_ID, USERS_ID, HELP_TITLE, HELP_CARE_TYPE, HELP_CARE_DATE, HELP_CREATED_AT
     FROM HELP_BOARD
     WHERE USERS_ID = #{usersId}
     ORDER BY HELP_CARE_DATE)

     WHERE ROWNUM <= #{page.page} * #{page.size}

     )
    WHERE RNUM > (#{page.page} - 1) * #{page.size}

SELECT COUNT(*)
FROM HELP_BOARD
WHERE USERS_ID=#{usersId}

      SELECT RNUM, HELP_OFFER_ID, HELP_OFFER_STATUS, HELP_OFFER_ENROLL_DATE, HELP_ID, USERS_ID
FROM(SELECT ROWNUM AS RNUM, HELP_OFFER_ID, HELP_OFFER_STATUS, HELP_OFFER_ENROLL_DATE, HELP_ID, USERS_ID
          FROM(
        SELECT HELP_OFFER_ID, HELP_OFFER_STATUS, HELP_OFFER_ENROLL_DATE, HELP_ID, USERS_ID
        FROM HELP_OFFER
        WHERE USERS_ID = #{usersId}
        ORDER BY HELP_OFFER_ENROLL_DATE)

      WHERE ROWNUM <= #{page.page} * #{page.size}

        )
      WHERE RNUM > (#{page.page} - 1) * #{page.size}

SELECT COUNT(*)
FROM HELP_OFFER
WHERE USERS_ID=#{usersId}

SELECT RNUM, ZIP_ID, ZIP_TITLE, ZIP_CREATED_AT, USERS_ID
    FROM(
     SELECT ROWNUM AS RNUM, ZIP_ID, ZIP_TITLE, ZIP_CREATED_AT, USERS_ID
     FROM(SELECT ZIP_ID, ZIP_TITLE, ZIP_CREATED_AT, USERS_ID
  FROM ZIP_BOARD
  WHERE USERS_ID = #{usersId})

 WHERE ROWNUM <= #{page.page} * #{page.size}

  )
 WHERE RNUM > (#{page.page} - 1) * #{page.size}

SELECT COUNT(*)
FROM ZIP_BOARD
WHERE USERS_ID=#{usersId}

SELECT RNUM, ZIP_ANSWER_ID, ZIP_TITLE, ZIP_ANSWER_CONTENT, ZIP_ANSWER_CREATED_AT, USERS_ID, ZIP_ID FROM(SELECT ROWNUM AS RNUM, ZIP_ANSWER_ID, ZIP_TITLE, ZIP_ANSWER_CONTENT, ZIP_ANSWER_CREATED_AT, USERS_ID, ZIP_ID FROM( SELECT za.ZIP_ANSWER_ID, zb.ZIP_TITLE, za.ZIP_ANSWER_CONTENT, za.ZIP_ANSWER_CREATED_AT, za.USERS_ID, zb.ZIP_ID FROM ZIP_ANSWER za JOIN ZIP_BOARD zb ON za.ZIP_ID = zb.ZIP_ID WHERE za.USERS_ID = #{usersId})
WHERE ROWNUM <= #{page.page} * #{page.size}
) WHERE RNUM > (#{page.page} - 1) * #{page.size}

SELECT COUNT(*)
FROM ZIP_ANSWER
WHERE USERS_ID=#{usersId}

SELECT RNUM, REVIEW_ID, REVIEW_TITLE, REVIEW_CREATED_AT, USERS_ID
FROM(SELECT  ROWNUM AS RNUM, REVIEW_ID, REVIEW_TITLE, REVIEW_CREATED_AT, USERS_ID
FROM ( SELECT  R.REVIEW_ID, R.REVIEW_TITLE, R.REVIEW_CREATED_AT, HO.USERS_ID
        FROM REVIEW R
                 JOIN HELP_OFFER HO ON R.HELP_OFFER_ID = HO.HELP_OFFER_ID
        WHERE HO.USERS_ID = #{usersId})

WHERE ROWNUM <= #{page.page} * #{page.size}

        )
WHERE RNUM > (#{page.page} - 1) * #{page.size}

SELECT COUNT(*)
FROM REVIEW
WHERE USERS_ID=#{usersId}

SELECT RNUM, REVIEW_ID, REVIEW_TITLE, REVIEW_CREATED_AT, USERS_ID
FROM (SELECT ROWNUM AS RNUM, REVIEW_ID, REVIEW_TITLE, REVIEW_CREATED_AT, USERS_ID
      FROM (SELECT R.REVIEW_ID, R.REVIEW_TITLE, R.REVIEW_CREATED_AT, R.USERS_ID
            FROM REVIEW R
            WHERE R.USERS_ID = #{usersId})

      WHERE ROWNUM <= #{page.page} * #{page.size}

)
WHERE RNUM > (#{page.page} - 1) * #{page.size}

SELECT COUNT(*)
FROM REVIEW
WHERE USERS_ID=#{usersId}

SELECT RNUM, DN_ID, DN_PRDUCT_CATEGORY, DN_TITLE, USERS_ID
FROM (SELECT ROWNUM AS RNUM, DN_ID, DN_PRDUCT_CATEGORY, DN_TITLE, USERS_ID
      FROM (SELECT DN_ID, DN_PRDUCT_CATEGORY, DN_TITLE, USERS_ID
            FROM DN_BOARD
            WHERE USERS_ID = #{usersId})

      WHERE ROWNUM <= #{page.page} * #{page.size}

)
WHERE RNUM > (#{page.page} - 1) * #{page.size}

SELECT COUNT(*)
FROM DN_BOARD
WHERE USERS_ID=#{usersId}

SELECT RNUM, PRODUCT_ID, PRODUCT_NAME, PRODUCT_PRICE, USERS_ID
FROM (SELECT ROWNUM AS RNUM, PRODUCT_ID, PRODUCT_NAME, PRODUCT_PRICE, USERS_ID
      FROM (SELECT P.PRODUCT_ID, P.PRODUCT_NAME, P.PRODUCT_PRICE, DS.USERS_ID
            FROM PRODUCT P
                     JOIN DN_SELL DS ON P.PRODUCT_ID = DS.PRODUCT_ID

            WHERE DS.USERS_ID = #{usersId})

      WHERE ROWNUM <= #{page.page} * #{page.size}

         )
 WHERE RNUM > (#{page.page} - 1) * #{page.size}

SELECT COUNT(*)
FROM DN_SELL
WHERE USERS_ID=#{usersId}
